mapper.select:
SELECT schedule_date, start_time, end_time, title, type_id, description, create_time, update_time
FROM schedule_record
WHERE schedule_date = #{scheduleDate}

SELECT schedule_date, start_time, end_time, title, type_id, description, create_time, update_time
FROM schedule_record
WHERE type_id = #{typeId}

SELECT schedule_date, start_time, end_time, title, type_id, description, create_time, update_time
FROM schedule_record
WHERE type_id = #{scheduleType.typeId} AND schedule_date = #{scheduleDate}

SELECT schedule_date, start_time, end_time, title, type_id, description, create_time, update_time
FROM schedule_record
WHERE start_time = #{startTime} AND end_time = #{endTime}

mapper.update:
UPDATE schedule_record
SET title = #{schedule.title}, 
    type_id = #{schedule.scheduleType.typeId}, 
    description = #{schedule.description}, 
    update_time = #{schedule.updateTime}
WHERE start_time = #{startTime} AND end_time = #{endTime} AND schedule_date = #{scheduleDate}
