mapper.select:
SELECT type_id AS typeId, type_name AS typeName, type_description AS typeDescription
FROM schedule_type
WHERE type_id = #{typeId}

mapper.update:
UPDATE schedule_type
SET type_name = #{typeName}, type_description = #{typeDescription}
WHERE type_id = #{typeId}

UPDATE schedule_record
SET title = #{schedule.title}, 
    type_id = #{schedule.type.typeId}, 
    schedule_description = #{schedule.description}, 
    update_time = #{schedule.updateTime}
WHERE start_time = #{startTime} AND end_time = #{endTime}
